import {
  PerspectiveCamera, Scene, WebGLRenderer, AmbientLight, Group, DoubleSide, TextureLoader,
  Vector3, Shape, ShapeGeometry, MeshToonMaterial, Mesh, PlaneGeometry, MeshBasicMaterial
} from 'three'

export function init() {
  return {
    camera: initCam(),
    scene: initScene(),
    renderer: initRenderer()
  }
}

export function initCube(order = 3, size = 1) {
  const colors = ['#096dd9', '#65c89b', '#ff5500', '#444444', '#ffffff', '#fff700']
  const faces = initFaces(order, colors, size)
  return faces
}

function initCam() {
  const camera = new PerspectiveCamera(45, 1, 0.1, 1000)
  camera.position.set(-3, 5, 7)
  return camera
}

function initScene() {
  const scene = new Scene()
  const light = new AmbientLight(0xeeeeee)
  scene.add(light)
  return scene
}

function initRenderer() {
  const renderer = new WebGLRenderer({ antialias: true })
  renderer.setClearColor('#aadddd', 1.0)
  return renderer
}

function initFaces(order, colors, size) {
  const faces = new Group()
  const faceGeometry = initPlaneGeometry(size)

  const offset = (order - 1) / 2
  // fix-Y axis
  for (let x = -offset; x <= offset; x++) {
    for (let z = -offset; z <= offset; z++) {
      // top
      let info = {
        color: colors[0],
        // add 0.5 since it's the pos of the face
        position: new Vector3(x * size, (offset + 0.5) * size, z * size),
        normal: new Vector3(0, 1, 0)
      }
      if ((x === 0 && z === 0) || (x === -0.5 && z === -0.5)) {
        const face = packing(size, faceGeometry, info)
        loadImg('top', face)
        faces.add(face)
      } else {
        faces.add(packing(size, faceGeometry, info))
      }
      // bottom
      info = {
        color: colors[1],
        position: new Vector3(x * size, -(offset + 0.5) * size, z * size),
        normal: new Vector3(0, -1, 0)
      }
      if ((x === 0 && z === 0) || (x === -0.5 && z === -0.5)) {
        const face = packing(size, faceGeometry, info)
        loadImg('bottom', face)
        faces.add(face)
      } else {
        faces.add(packing(size, faceGeometry, info))
      }
    }
  }
  // fix X-axis
  for (let y = -offset; y <= offset; y++) {
    for (let z = -offset; z <= offset; z++) {
      // left
      let info = {
        color: colors[2],
        position: new Vector3(-(offset + 0.5) * size, y * size, z * size),
        normal: new Vector3(-1, 0, 0)
      }
      if ((y === 0 && z === 0) || (y === -0.5 && z === -0.5)) {
        const face = packing(size, faceGeometry, info)
        loadImg('left', face)
        faces.add(face)
      } else {
        faces.add(packing(size, faceGeometry, info))
      }
      // right
      info = {
        color: colors[3],
        position: new Vector3((offset + 0.5) * size, y * size, z * size),
        normal: new Vector3(1, 0, 0)
      }
      if ((y === 0 && z === 0) || (y === -0.5 && z === -0.5)) {
        const face = packing(size, faceGeometry, info)
        loadImg('right', face)
        faces.add(face)
      } else {
        faces.add(packing(size, faceGeometry, info))
      }
    }
  }
  // fix Z-axis
  for (let x = -offset; x <= offset; x++) {
    for (let y = -offset; y <= offset; y++) {
      // front
      let info = {
        color: colors[4],
        position: new Vector3(x * size, y * size, (offset + 0.5) * size),
        normal: new Vector3(0, 0, 1)
      }
      if ((x === 0 && y === 0) || (x === -0.5 && y === -0.5)) {
        const face = packing(size, faceGeometry, info)
        loadImg('front', face)
        faces.add(face)
      } else {
        faces.add(packing(size, faceGeometry, info))
      }
      // back
      info = {
        color: colors[5],
        position: new Vector3(x * size, y * size, -(offset + 0.5) * size),
        normal: new Vector3(0, 0, -1)
      }
      if ((x === 0 && y === 0) || (x === -0.5 && y === -0.5)) {
        const face = packing(size, faceGeometry, info)
        loadImg('back', face)
        faces.add(face)
      } else {
        faces.add(packing(size, faceGeometry, info))
      }
    }
  }
  return faces
}

function initBaseMesh(size) {
  const baseGeo = new PlaneGeometry(size, size)
  const baseMat = new MeshToonMaterial({ color: 'black', side: DoubleSide })
  return new Mesh(baseGeo, baseMat)
}

function initMesh(color, geometry) {
  const material = new MeshToonMaterial({ color, emissive: color })
  const mesh = new Mesh(geometry, material)
  // make space for the base mesh
  mesh.scale.set(0.9, 0.9, 1)
  // avoid overlay
  mesh.position.setZ(0.01)

  return mesh
}

function initPlaneGeometry(size_) {
  const x = 0
  const y = 0
  const size = size_ / 2
  const len = size * 0.4

  // draw a plane with rounded corners
  const faceShape = new Shape()
  // start point: up-left
  faceShape.moveTo(x - len, y + size)
  // up straight part and up-right corner
  faceShape.lineTo(x + len, y + size)
  // use corner as the control point
  faceShape.bezierCurveTo(x + size, y + size, x + size, y + size, x + size, y + len)
  // same way to draw other corners
  faceShape.lineTo(x + size, y - len)
  faceShape.bezierCurveTo(x + size, y - size, x + size, y - size, x + len, y - size)
  faceShape.lineTo(x - len, y - size)
  faceShape.bezierCurveTo(x - size, y - size, x - size, y - size, x - size, y - len)
  faceShape.lineTo(x - size, y + len)
  faceShape.bezierCurveTo(x - size, y + size, x - size, y + size, x - len, y + size)

  return new ShapeGeometry(faceShape)
}

function packing(size, geo, info) {
  const face = new Group()
  const baseMesh = initBaseMesh(size)
  const faceMesh = initMesh(info.color, geo)
  face.add(baseMesh)
  face.add(faceMesh)
  face.info = info
  face.position.set(info.position.x, info.position.y, info.position.z)
  face.lookAt(info.position.clone().add(info.normal))
  return face
}

function loadImg(side, face) {
  let img
  if (side === 'right') img = 'data:image/jpg;base64,'
  if (side === 'bottom') img = 'data:image/jpg;base64,'
  if (side === 'top') img = 'data:image/jpg;base64,'
  if (side === 'left') img = 'data:image/jpg;base64,'
  if (side === 'front') img = 'data:image/jpg;base64,'
  if (side === 'back') img = 'data:image/jpg;base64,'
  new TextureLoader().load(img, (texture) => {
    const mesh = new Mesh(new PlaneGeometry(1, 1, 1), new MeshBasicMaterial({ map: texture }))
    mesh.position.setZ(0.02)
    mesh.scale.set(0.8, 0.8, 0.8)
    face.add(mesh)
  })
}
